mapper.select:
SELECT A.feed_id AS feedId, A.writer_id AS writerId, A.contents, B.nm AS writerNm, B.pic AS writerPic, A.location, A.created_at AS createdAt, A.updated_at AS updatedAt, IF(C.feed_id IS NULL, 0, 1) AS isFav FROM feed A JOIN user B ON A.writer_id = B.user_id LEFT JOIN feed_favorite C ON A.feed_id = C.feed_id AND C.user_id = #{signedUserId}
WHERE A.writer_id = #{profileUserId}
ORDER BY A.feed_id DESC LIMIT #{startIdx}, #{size}

SELECT pic
FROM feed_pics
WHERE feed_id = #{feedId}
